import PhonebookContext from './PhonebookContext'

function UpdateState() {

    const { phones, updatePhones } = useContext(PhonebookContext);
    const additionalPhones = [
        { name: "Julius", number: "+256771444XXX" },
        { name: "Abudi", number: "+256704998XXX" },
        { name: "Janet", number: "+256763554XXX" }
    ];

    return (
        <button onClick={() => {
            updatePhones({...phones,additionalPhones})
        }}>Update state</button>
    )
}

export default UpdateState
